mapper.select:
SELECT
	*
FROM
	SWEET_MESSAGE
WHERE
	INV_SEQ = #{invSeq}
ORDER BY
	DATETIME_REGISTER DESC
LIMIT #{pageStart}, #{perPageNum}
;

mapper.update:
UPDATE
	INVITATION_LIST
SET

	USE_LS = #{useFlag}


	USE_PYEBAEK = #{useFlag}


	USE_G = #{useFlag}


	USE_SM = #{useFlag}

	, DATETIME_UPDATE = NOW()
WHERE
	SEQ = #{invSeq}
;

UPDATE

	LOVE_STORY


	GALLERY

SET
	IS_DELETE = TRUE
	, DATETIME_UPDATE = NOW()
WHERE
	INV_SEQ = #{invSeq}
	AND SEQ NOT IN #{item}
;

UPDATE
	INVITATION_LIST
SET
	VISIBLE = #{visible}
	, DATE_BEGIN = #{periodBegin}
	, DATE_END = #{periodEnd}
	, FORMCODE = #{formCode}
	, DATETIME_UPDATE = NOW()
WHERE
	SEQ = #{seq}
;

UPDATE
	MAIN_INFO
SET
	DATE_WEDDING = #{dateWedding}
	, TIME_WEDDING = #{timeWedding}
	, X_PLACE = #{placeX}
	, Y_PLACE = #{placeY}
	, ADDRESS = #{address}
	, CONTENT_GROOM = #{contentGroom}
	, CONTENT_BRIDE = #{contentBride}
	, SEQ_IMG_MAIN = #{seqImgMain}
	, YN_EACH_IMAGE = #{useEachImage}
	, SEQ_IMG_GROOM = #{seqImgGroom}
	, SEQ_IMG_BRIDE = #{seqImgBride}
	, DATETIME_UPDATE = NOW()
WHERE
	SEQ = #{seq}
;

UPDATE
	LOVE_STORY
SET
	ORDER_SEQ = #{orderSeq}
	, DATE_STORY = #{dateStory}
	, TITLE = #{title}
	, CONTENT = #{content}
	, DATETIME_UPDATE = NOW()
WHERE
	SEQ = #{seq}
;

UPDATE
	WHEN_WHERE
SET
	TITLE = #{title}
	, CONTENT = #{content}

	, FLAG_PYEBAEK = #{flagPyebaek}
	, DATE_WW = #{dateWedding}
	, TIME_WW = #{timeWedding}
	, X_PLACE = #{placeX}
	, Y_PLACE = #{placeY}
	, ADDRESS = #{address}

	, DATETIME_UPDATE = NOW()
WHERE
	SEQ = #{seq}
;

UPDATE
	GALLERY
SET
	ORDER_SEQ = #{orderSeq}
	, DATETIME_UPDATE = NOW()
WHERE
	SEQ = #{seq}
;

UPDATE
	SWEET_MESSAGE
SET
	IS_DELETE = #{isDelete}
	, DATETIME_UPDATE = NOW()
WHERE
	SEQ = #{seq}
;

mapper.select:
SELECT IL.SEQ AS invSeq , IL.ID AS id , (SELECT NAME FROM USERES U WHERE U.ID = IL.ID) AS name , IL.VISIBLE AS visible , IL.DATE_BEGIN AS periodBegin , IL.DATE_END AS periodEnd , (SELECT DATE_WEDDING FROM MAIN_INFO MI WHERE MI.INV_SEQ = IL.SEQ) AS weddingDate FROM INVITATION_LIST IL WHERE 1=1
AND IL.ID LIKE CONCAT('%', #{id}, '%')
AND (SELECT NAME FROM USERES U WHERE U.ID = IL.ID) LIKE CONCAT('%', #{name}, '%')
AND (SELECT DATE_WEDDING FROM MAIN_INFO MI WHERE MI.INV_SEQ = IL.SEQ) >= #{beginDate} AND (SELECT DATE_WEDDING FROM MAIN_INFO MI WHERE MI.INV_SEQ = IL.SEQ) <= #{endDate}
ORDER BY IL.SEQ DESC LIMIT #{pageStart}, #{perPageNum} ;

SELECT COUNT(IL.SEQ) FROM INVITATION_LIST IL WHERE 1=1
AND IL.ID LIKE CONCAT('%', #{id}, '%')
AND (SELECT NAME FROM USERES U WHERE U.ID = IL.ID) LIKE CONCAT('%', #{name}, '%')
AND (SELECT DATE_WEDDING FROM MAIN_INFO MI WHERE MI.INV_SEQ = IL.SEQ) >= #{beginDate} AND (SELECT DATE_WEDDING FROM MAIN_INFO MI WHERE MI.INV_SEQ = IL.SEQ) <= #{endDate}
;
